/* agent-frontmatter:start
AGENT: Message part view orchestrator
PURPOSE: Route different tool and content types to their specific UI components
USAGE: <MessagePart part={part} isStreaming={isStreaming} />
EXPORTS: MessagePart
FEATURES:
  - Dispatches tool parts to specialized renderers (bash, read, write, etc.)
  - Renders text content with markdown support
  - Handles reasoning blocks with collapsible UI
  - Supports all AgentStart tool visualizations
SEARCHABLE: message view, tool renderer, part dispatcher, ui orchestrator
agent-frontmatter:end */

import type { AgentStartToolSet, BaseMessagePart } from "agentstart/agent";
import { memo } from "react";
import isEqual from "react-fast-compare";
import { Response } from "../response";
import { Bash } from "./bash";
import { EditFile } from "./edit";
import { Glob } from "./glob";
import { Grep } from "./grep";
import { Ls } from "./ls";
import { ReadFile } from "./read";
import {
  Reasoning as BaseReasoning,
  getThinkingMessage,
  ReasoningContent,
  ReasoningTrigger,
} from "./reasoning";
import { Todo } from "./todo";
import { WriteFile } from "./write";

// ========= Add your custom tool types here =========
// To add new tools, simply add them to the type below
// Example:
// type ExtendedToolSet = AgentStartToolSet & {
//   myCustomTool?: any;  // Add other custom tools
// }
type ExtendedToolSet = AgentStartToolSet;
// ================================================

interface Props {
  part: BaseMessagePart<ExtendedToolSet>;
  isStreaming: boolean;
}

export const MessagePart = memo<Props>(({ part, isStreaming }) => {
  switch (part.type) {
    case "tool-read": {
      return <ReadFile part={part} />;
    }
    case "tool-write":
      return <WriteFile part={part} />;
    case "tool-edit":
      return <EditFile part={part} />;
    case "tool-bash":
      return <Bash part={part} />;
    case "tool-glob":
      return <Glob part={part} />;
    case "tool-grep":
      return <Grep part={part} />;
    case "tool-ls":
      return <Ls part={part} />;
    case "tool-todoRead":
    case "tool-todoWrite":
      return <Todo part={part} />;

    case "reasoning":
      return (
        <Reasoning
          className="w-full"
          isStreaming={isStreaming}
          text={part.text}
        />
      );
    case "text":
      return <Response>{part.text}</Response>;
    default:
      return null;
  }
}, isEqual);

function Reasoning({
  className,
  text,
  isStreaming,
}: {
  className?: string;
  text: string;
  isStreaming: boolean;
}) {
  return (
    <BaseReasoning
      className={className}
      isStreaming={isStreaming}
      defaultOpen={false}
    >
      <ReasoningTrigger>{getThinkingMessage(isStreaming)}</ReasoningTrigger>
      <ReasoningContent>{text}</ReasoningContent>
    </BaseReasoning>
  );
}

/* agent-frontmatter:start
AGENT: Bash tool UI component
PURPOSE: Display shell command execution results with stdout/stderr output
USAGE: <Bash part={toolPart} />
EXPORTS: Bash, BashProps
FEATURES:
  - Shows executed command with description
  - Renders stdout and stderr with syntax highlighting
  - Displays execution duration and exit code
  - Distinguishes between success and error states
SEARCHABLE: bash tool, shell command ui, terminal output view
agent-frontmatter:end */

import { CommandIcon } from "@phosphor-icons/react";
import type { Tools } from "agentstart/agent";
import type { InferUITools, ToolUIPart } from "ai";
import { useMemo } from "react";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { CodeBlock } from "../code-block";
import { Steps, StepsContent, StepsItem, StepsTrigger } from "../steps";

export interface BashProps {
  part: ToolUIPart<InferUITools<Pick<Tools, "bash">>>;
}

export function Bash({ part: { state, input, output } }: BashProps) {
  const hasOutput = output?.metadata?.stdout || output?.metadata?.stderr;
  const formatDuration = (ms?: number) => {
    if (!ms) return "";
    return ms < 1000 ? `${ms}ms` : `${(ms / 1000).toFixed(2)}s`;
  };
  const isLoading = ["input-streaming", "input-available"].includes(state);

  const title = useMemo(() => {
    if (!input?.command) return <span>Ran command</span>;
    return (
      <div className="flex w-full items-center gap-2 overflow-hidden">
        Ran command:
        <Tooltip>
          <TooltipTrigger
            render={
              <span className="flex-1 truncate text-left font-mono text-foreground">
                {input?.command}
              </span>
            }
          />
          <TooltipContent align="start">
            <div className="flex flex-col gap-1">
              {input?.description && (
                <span className="text-muted-foreground text-xs">
                  {input.description}
                </span>
              )}

              {/* Exit code and duration */}
              {state === "output-available" && output && (
                <div className="flex items-center gap-4 text-muted-foreground text-xs">
                  {output.metadata?.exitCode !== undefined && (
                    <span
                      className={
                        output.metadata.exitCode === 0
                          ? undefined
                          : "text-yellow-600"
                      }
                    >
                      Exit code: {output.metadata.exitCode}
                    </span>
                  )}
                  {output.metadata?.duration && (
                    <span>
                      Duration: {formatDuration(output.metadata.duration)}
                    </span>
                  )}
                  {/* {output.metadata?.commitHash && (
            <CommitHash hash={output.metadata.commitHash} />
          )} */}
                </div>
              )}
            </div>
          </TooltipContent>
        </Tooltip>
      </div>
    );
  }, [input?.command, input?.description, output, state]);

  return (
    <Steps data-tool-bash>
      <StepsTrigger
        loading={isLoading}
        error={output?.status === "error" || Boolean(output?.error)}
        leftIcon={<CommandIcon weight="duotone" className="size-4" />}
      >
        {title}
      </StepsTrigger>
      <StepsContent>
        <StepsItem>
          <div className="space-x-2">
            <span className="text-sm">$</span>
            <span className="font-mono text-foreground text-sm">
              {input?.command}
            </span>
          </div>
        </StepsItem>

        {/* Status indicators */}
        {isLoading && (
          <StepsItem className="flex items-center gap-2 text-muted-foreground text-xs">
            <span>Executing command...</span>
          </StepsItem>
        )}

        {/* Standard output */}
        {output?.metadata?.stdout && (
          <StepsItem>
            <CodeBlock code={output.metadata.stdout} language="bash" />
          </StepsItem>
        )}

        {/* No output message */}
        {state === "output-available" && output && !hasOutput && (
          <StepsItem>Command executed successfully (no output)</StepsItem>
        )}

        {/* Standard error */}
        {output?.metadata?.stderr && (
          <StepsItem>
            <CodeBlock
              code={output.metadata.stderr}
              language="bash"
              className="border-red-200 text-xs dark:border-red-900"
            />
          </StepsItem>
        )}
      </StepsContent>
    </Steps>
  );
}

/* agent-frontmatter:start
AGENT: Glob tool UI component
PURPOSE: Display file pattern matching results from glob tool
USAGE: <Glob part={toolPart} />
EXPORTS: Glob, GlobProps
FEATURES:
  - Renders matched file and directory paths
  - Color-coded icons for files vs directories
  - Shows pattern used and match count
  - Highlights filename vs directory path distinction
SEARCHABLE: glob tool, file pattern match ui, file search results
agent-frontmatter:end */

import {
  FileIcon,
  FileMagnifyingGlassIcon,
  FolderIcon,
} from "@phosphor-icons/react";
import type { Tools } from "agentstart/agent";
import type { InferUITools, ToolUIPart } from "ai";
import { useCallback, useMemo } from "react";
import { Shimmer } from "../shimmer";
import { Steps, StepsContent, StepsItem, StepsTrigger } from "../steps";

export interface GlobProps {
  part: ToolUIPart<InferUITools<Pick<Tools, "glob">>>;
}

export function Glob({ part: { state, input, output } }: GlobProps) {
  const isLoading = ["input-streaming", "input-available"].includes(state);

  const renderMatch = useCallback((match: string) => {
    const isDirectory = match.endsWith("/");
    const cleanPath = isDirectory ? match.slice(0, -1) : match;
    const parts = cleanPath.split("/");
    const fileName = parts[parts.length - 1];
    const dirPath = parts.slice(0, -1).join("/");

    return (
      <div className="flex items-center gap-2 py-0.5 text-xs">
        {isDirectory ? (
          <FolderIcon className="size-4" weight="duotone" />
        ) : (
          <FileIcon className="size-4" weight="duotone" />
        )}
        <span>
          {dirPath && <span className="text-muted-foreground">{dirPath}/</span>}
          <span className="font-medium">{fileName}</span>
        </span>
      </div>
    );
  }, []);

  const results = useMemo(() => {
    if (!Array.isArray(output?.metadata?.matches)) return null;

    const matches = output.metadata.matches as string[];
    const totalMatches =
      typeof output.metadata?.count === "number"
        ? output.metadata.count
        : matches.length;

    return (
      <div className="flex flex-col gap-2">
        {/* Pattern and path */}
        <div className="flex flex-col gap-1">
          <div className="text-xs">
            <span className="text-muted-foreground">Pattern: </span>
            <code className="rounded bg-muted/50 px-1 py-0.5 font-mono">
              {input?.pattern}
            </code>
          </div>
          {input?.path && (
            <div className="text-xs">
              <span className="text-muted-foreground">In: </span>
              <code className="font-mono">{input.path}</code>
            </div>
          )}
        </div>

        {/* Results */}
        <div className="mt-1">
          <div className="mb-1 text-muted-foreground text-xs">
            Found {totalMatches} match{totalMatches !== 1 ? "es" : ""}
          </div>
          {matches.length > 0 ? (
            <div className="max-h-[300px] overflow-y-auto border-muted border-l-2 pl-2">
              {matches.slice(0, 100).map((match, index) => (
                <div key={`${match}-${index}`}>{renderMatch(match)}</div>
              ))}
              {matches.length > 100 && (
                <div className="mt-2 text-muted-foreground text-xs">
                  ... and {matches.length - 100} more
                </div>
              )}
            </div>
          ) : (
            <span className="text-muted-foreground text-xs italic">
              No files found matching this pattern
            </span>
          )}
        </div>
      </div>
    );
  }, [input?.path, input?.pattern, output?.metadata, renderMatch]);

  return (
    <Steps data-tool-glob>
      <StepsTrigger
        leftIcon={
          <FileMagnifyingGlassIcon weight="duotone" className="size-4" />
        }
        loading={isLoading}
        error={output?.status === "error" || Boolean(output?.error)}
      >
        <div className="flex items-center gap-2">
          <span>Glob pattern: </span>
          <code className="rounded bg-muted/50 px-1 py-0.5 font-mono text-xs">
            {input?.pattern}
          </code>
        </div>
      </StepsTrigger>
      <StepsContent>
        {isLoading && (
          <StepsItem className="flex items-center gap-2 text-muted-foreground text-xs">
            <Shimmer>Searching files...</Shimmer>
          </StepsItem>
        )}
        {results && <StepsItem>{results}</StepsItem>}
        {output?.error?.message && (
          <StepsItem className="text-red-600 text-xs">
            {output.error.message}
          </StepsItem>
        )}
      </StepsContent>
    </Steps>
  );
}

/* agent-frontmatter:start
AGENT: Grep tool UI component
PURPOSE: Display code search results from grep tool with syntax highlighting
USAGE: <Grep part={toolPart} />
EXPORTS: Grep, GrepProps
FEATURES:
  - Shows search pattern and matched content
  - Supports multiple output modes (content/files/count)
  - Syntax highlighting for matched code lines
  - Displays file paths and line numbers with matches
SEARCHABLE: grep tool, code search ui, content search results
agent-frontmatter:end */

import { FileIcon, HashIcon } from "@phosphor-icons/react";
import type { Tools } from "agentstart/agent";
import type { InferUITools, ToolUIPart } from "ai";
import { CodeBlock } from "../code-block";
import { Shimmer } from "../shimmer";
import { Steps, StepsContent, StepsItem, StepsTrigger } from "../steps";

export interface GrepProps {
  part: ToolUIPart<InferUITools<Pick<Tools, "grep">>>;
}

export function Grep({ part: { state, input, output } }: GrepProps) {
  const isLoading = ["input-streaming", "input-available"].includes(state);

  const getOutputModeLabel = () => {
    switch (input?.outputMode) {
      case "content":
        return "Matching Lines";
      case "count":
        return "Match Counts";
      // case "files_with_matches":
      default:
        return "Matching Files";
    }
  };

  const renderFilesOutput = () => {
    const files = Array.isArray(output?.metadata?.files)
      ? (output.metadata.files as string[])
      : [];
    if (files.length === 0) {
      return (
        <span className="text-muted-foreground text-xs italic">
          No files found with matches
        </span>
      );
    }

    const maxFiles = 100;
    const displayFiles = files.slice(0, maxFiles);
    const hasMore = files.length > maxFiles;

    return (
      <div className="mt-2">
        <div className="mb-1 text-muted-foreground text-xs">
          Found in {output?.metadata?.fileCount ?? files.length} file
          {(output?.metadata?.fileCount ?? files.length) !== 1 && "s"}
        </div>
        <div className="max-h-[300px] overflow-y-auto border-muted border-l-2 pl-2">
          {displayFiles.map((file, index) => (
            <div
              key={`${file}-${index}`}
              className="flex items-center gap-2 py-0.5 text-xs"
            >
              <FileIcon className="size-4" weight="duotone" />
              <span className="font-mono">{file}</span>
            </div>
          ))}
          {hasMore && (
            <div className="mt-2 text-muted-foreground text-xs">
              {`// ... and ${files.length - maxFiles} more`}
            </div>
          )}
        </div>
      </div>
    );
  };

  const renderCountsOutput = () => {
    const counts = Array.isArray(output?.metadata?.counts)
      ? (output.metadata.counts as Array<{ filename: string; count: number }>)
      : [];
    if (counts.length === 0) {
      return (
        <span className="text-muted-foreground text-xs italic">
          No matches found
        </span>
      );
    }

    return (
      <div className="mt-2">
        <div className="mb-1 text-muted-foreground text-xs">
          Total: {output?.metadata?.totalMatches || 0} match
          {(output?.metadata?.totalMatches || 0) !== 1 && "es"} in{" "}
          {counts.length} file{counts.length !== 1 && "s"}
        </div>
        <div className="max-h-[300px] space-y-0.5 overflow-y-auto font-mono text-xs">
          {counts.map((item, index) => (
            <div
              key={`${item.filename}-${index}`}
              className="flex items-center gap-2 rounded px-1 py-0.5 hover:bg-muted/50"
            >
              <HashIcon className="size-4" weight="duotone" />
              <span className="w-12 text-right text-blue-600">
                {item.count}
              </span>
              <span>{item.filename}</span>
            </div>
          ))}
        </div>
      </div>
    );
  };

  const renderContentOutput = () => {
    const matches = Array.isArray(output?.metadata?.matches)
      ? (output.metadata.matches as string[])
      : [];
    if (matches.length === 0) {
      return (
        <span className="text-muted-foreground text-xs italic">
          No matching lines found
        </span>
      );
    }

    // Join all matches into a single string for the code block
    const content = matches.join("\n");

    return (
      <div className="mt-2">
        <div className="mb-1 text-muted-foreground text-xs">
          Found {output?.metadata?.matchCount || 0} match
          {(output?.metadata?.matchCount || 0) !== 1 && "es"} in{" "}
          {output?.metadata?.fileCount || 0} file
          {(output?.metadata?.fileCount || 0) !== 1 && "s"}
        </div>
        <CodeBlock code={content} language="markdown" />
      </div>
    );
  };

  const renderResults = () => {
    if (!output) return null;

    return (
      <div className="flex flex-col gap-2">
        {/* Pattern and options */}
        <div className="flex flex-col gap-1">
          <div className="text-xs">
            <span className="text-muted-foreground">Pattern: </span>
            <code className="rounded bg-muted/50 px-1 py-0.5 font-mono">
              {input?.pattern}
            </code>
          </div>
          {input?.path && (
            <div className="text-xs">
              <span className="text-muted-foreground">In: </span>
              <code className="font-mono">{input.path}</code>
            </div>
          )}
          {input?.outputMode && (
            <div className="text-xs">
              <span className="text-muted-foreground">Mode: </span>
              <span>{getOutputModeLabel()}</span>
            </div>
          )}
        </div>

        {/* Results based on output mode */}
        {input?.outputMode === "files_with_matches" || !input?.outputMode
          ? renderFilesOutput()
          : input?.outputMode === "count"
            ? renderCountsOutput()
            : renderContentOutput()}
      </div>
    );
  };

  return (
    <Steps data-tool-grep>
      <StepsTrigger
        leftIcon={<HashIcon weight="duotone" className="size-4" />}
        loading={isLoading}
        error={output?.status === "error" || Boolean(output?.error)}
      >
        <div className="flex items-center gap-2">
          <span>Search pattern: </span>
          <code className="rounded bg-muted/50 px-1 py-0.5 font-mono text-xs">
            {input?.pattern}
          </code>
        </div>
      </StepsTrigger>
      <StepsContent>
        {isLoading && (
          <StepsItem className="flex items-center gap-2 text-muted-foreground text-xs">
            <Shimmer>Searching code...</Shimmer>
          </StepsItem>
        )}
        <StepsItem>{renderResults()}</StepsItem>
        {output?.error?.message && (
          <StepsItem className="text-red-600 text-xs">
            {output.error.message}
          </StepsItem>
        )}
      </StepsContent>
    </Steps>
  );
}

/* agent-frontmatter:start
AGENT: Ls tool UI component
PURPOSE: Display directory listings from the ls tool with file metadata
USAGE: <Ls part={toolPart} />
EXPORTS: Ls, LsProps
FEATURES:
  - Renders file/directory/symlink entries with appropriate icons
  - Shows file sizes and modification dates
  - Distinguishes file types with color-coded icons
  - Displays tool execution state (pending/success/error)
SEARCHABLE: ls tool, directory listing, file browser ui
agent-frontmatter:end */

import { formatDate, formatSize } from "@agentstart/utils";
import { FileIcon, FolderIcon, LinkIcon } from "@phosphor-icons/react";
import type { Tools } from "agentstart/agent";
import type { InferUITools, ToolUIPart } from "ai";
import { useCallback, useMemo } from "react";
import { Shimmer } from "../shimmer";
import { Steps, StepsContent, StepsItem, StepsTrigger } from "../steps";

export interface LsProps {
  part: ToolUIPart<InferUITools<Pick<Tools, "ls">>>;
}

export function Ls({ part: { state, input, output } }: LsProps) {
  const isLoading = ["input-streaming", "input-available"].includes(state);

  const getIcon = useCallback((entryType: "file" | "directory" | "symlink") => {
    switch (entryType) {
      case "directory":
        return <FolderIcon className="size-4" weight="duotone" />;
      case "symlink":
        return <LinkIcon className="size-4" weight="duotone" />;
      default:
        return <FileIcon className="size-4" weight="duotone" />;
    }
  }, []);

  const entries = useMemo(() => {
    if (!Array.isArray(output?.metadata?.entries)) return null;
    type LsEntry = {
      type: "file" | "directory" | "symlink";
      name: string;
      size: number;
      modifiedTime: number;
    };
    const entries = output.metadata.entries as LsEntry[];

    return (
      <div>
        {entries.length > 0 ? (
          <div className="space-y-0.5 font-mono text-xs">
            {entries.map((entry, index) => (
              <div
                key={`${entry.name}-${index}`}
                className="flex items-center gap-2 rounded px-1 py-0.5 hover:bg-muted/50"
              >
                {getIcon(entry.type)}
                <span className="w-12 text-right text-muted-foreground">
                  {entry.type === "directory" ? "-" : formatSize(entry.size)}
                </span>
                <span className="text-muted-foreground">
                  {formatDate(entry.modifiedTime)}
                </span>
                <span
                  className={
                    entry.type === "directory"
                      ? "font-medium text-muted-foreground"
                      : "text-foreground"
                  }
                >
                  {entry.name}
                  {entry.type === "directory" && "/"}
                  {entry.type === "symlink" && " →"}
                </span>
              </div>
            ))}
          </div>
        ) : (
          <span className="text-muted-foreground text-xs italic">
            Directory is empty
          </span>
        )}
      </div>
    );
  }, [getIcon, output?.metadata]);

  return (
    <Steps data-tool-ls>
      <StepsTrigger
        leftIcon={<FolderIcon weight="duotone" className="size-4" />}
        loading={isLoading}
        error={output?.status === "error" || Boolean(output?.error)}
      >
        <div className="flex items-center gap-2">
          <span>Directory: </span>
          <code className="font-mono text-xs">{input?.path || "/"}</code>
          <div className="text-muted-foreground text-xs">
            {output?.metadata?.count || 0} item
            {(output?.metadata?.count || 0) !== 1 && "s"}
          </div>
        </div>
      </StepsTrigger>
      <StepsContent>
        {isLoading && (
          <StepsItem className="flex items-center gap-2 text-muted-foreground text-xs">
            <Shimmer>Listing directory...</Shimmer>
          </StepsItem>
        )}
        {entries && <StepsItem>{entries}</StepsItem>}
        {output?.error?.message && (
          <StepsItem className="text-red-600 text-xs">
            {output.error.message}
          </StepsItem>
        )}
      </StepsContent>
    </Steps>
  );
}

/* agent-frontmatter:start
AGENT: Read file tool UI component
PURPOSE: Display file reading operations with syntax-highlighted content
USAGE: <ReadFile part={toolPart} />
EXPORTS: ReadFile, ReadFileProps
FEATURES:
  - Shows file path and reading range (offset/limit)
  - Syntax highlighting based on file extension
  - Displays file content with line numbers
  - Handles partial file reads with range indicators
SEARCHABLE: read tool, file view ui, code viewer
agent-frontmatter:end */

import { getLanguageFromFilePath } from "@agentstart/utils";
import { SunglassesIcon } from "@phosphor-icons/react";
import type { Tools } from "agentstart/agent";
import type { InferUITools, ToolUIPart } from "ai";
import { useMemo } from "react";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { CodeBlock } from "../code-block";
import { Shimmer } from "../shimmer";
import { Steps, StepsContent, StepsItem, StepsTrigger } from "../steps";

export interface ReadFileProps {
  part: ToolUIPart<InferUITools<Pick<Tools, "read">>>;
}

export function ReadFile({ part: { state, input, output } }: ReadFileProps) {
  const fileName = input?.filePath?.split("/").pop() || input?.filePath;
  const readingRange = useMemo(() => {
    if (input?.offset === undefined && input?.limit === undefined) {
      return null;
    }
    const start = input.offset ?? 0;
    const end = input.limit ? start + input.limit : "end";
    return `Lines ${start + 1} - ${end}`;
  }, [input]);

  const preview = useMemo(() => {
    if (!output?.metadata?.content) return null;
    const lines = output.metadata.content.split("\n");
    const maxLines = 10;
    const hasMore = lines.length > maxLines;
    const language = getLanguageFromFilePath(input?.filePath);

    const previewLines = lines.slice(0, maxLines);
    const preview = hasMore
      ? [
          ...previewLines,
          `// ... and ${lines.length - maxLines} more lines`,
        ].join("\n")
      : previewLines.join("\n");

    return (
      <CodeBlock
        code={preview}
        copyCode={output.metadata.content}
        language={language}
      />
    );
  }, [output?.metadata?.content, input?.filePath]);

  const title = useMemo(() => {
    return (
      <Tooltip>
        <TooltipTrigger
          render={
            <div className="flex items-center gap-2">
              <span>{fileName}</span>
              {readingRange && (
                <span className="text-muted-foreground/60">{readingRange}</span>
              )}
            </div>
          }
        />
        {input?.filePath && <TooltipContent>{input.filePath}</TooltipContent>}
      </Tooltip>
    );
  }, [fileName, readingRange, input?.filePath]);
  const isLoading = ["input-streaming", "input-available"].includes(state);

  return (
    <Steps data-tool-read>
      <StepsTrigger
        leftIcon={<SunglassesIcon weight="duotone" className="size-4" />}
        loading={isLoading}
        error={output?.status === "error" || Boolean(output?.error)}
      >
        {title}
      </StepsTrigger>
      <StepsContent>
        {isLoading && (
          <StepsItem className="flex items-center gap-2 text-muted-foreground text-xs">
            <Shimmer>Reading file...</Shimmer>
          </StepsItem>
        )}
        {preview && <StepsItem>{preview}</StepsItem>}
        {output?.error?.message && (
          <StepsItem className="text-red-600 text-xs">
            {output.error.message}
          </StepsItem>
        )}
      </StepsContent>
    </Steps>
  );
}

/* agent-frontmatter:start
AGENT: Todo tool UI component
PURPOSE: Display task list from todo tool with status indicators
USAGE: <Todo part={toolPart} />
EXPORTS: Todo, TodoProps
FEATURES:
  - Shows todos grouped by status (inProgress, pending, completed)
  - Color-coded status icons (blue pulse, gray circle, green check)
  - Displays active/pending forms for each task
  - Error state visualization for failed operations
SEARCHABLE: todo tool, task list ui, progress tracker
agent-frontmatter:end */

import {
  CheckIcon,
  CircleIcon,
  ClockIcon,
  KanbanIcon,
  WarningIcon,
} from "@phosphor-icons/react";
import type { Tools } from "agentstart/agent";
import type { InferUITools, ToolUIPart } from "ai";
import type React from "react";
import { useMemo } from "react";
import { cn } from "@/lib/utils";
import { Shimmer } from "../shimmer";
import { Steps, StepsContent, StepsItem, StepsTrigger } from "../steps";

const statusOrder = ["inProgress", "pending", "completed"];

const statusIcons: Record<string, React.ReactNode> = {
  completed: <CheckIcon className="size-3.5 text-green-600" />,
  inProgress: <ClockIcon className="size-3.5 animate-pulse text-blue-600" />,
  pending: <CircleIcon className="size-3.5 text-muted-foreground" />,
};

const getStatusIcon = (status: string) => statusIcons[status] ?? null;

const priorityColors: Record<string, string> = {
  high: "text-red-600",
  medium: "text-yellow-600",
  low: "text-muted-foreground",
};

const getPriorityColor = (priority: string) =>
  priorityColors[priority] ?? "text-muted-foreground";

const priorityBadges: Record<string, string> = {
  high: "⚡",
  medium: "•",
  low: "○",
};

const getPriorityBadge = (priority: string) => (
  <span className={cn("text-xs", getPriorityColor(priority))}>
    {priorityBadges[priority] ?? ""}
  </span>
);

export interface TodoProps {
  part:
    | ToolUIPart<InferUITools<Pick<Tools, "todoRead">>>
    | ToolUIPart<InferUITools<Pick<Tools, "todoWrite">>>;
}

export function Todo({ part: { state, output, errorText } }: TodoProps) {
  const isLoading = ["input-streaming", "input-available"].includes(state);

  const todos = output?.metadata?.todos || [];

  const groupedTodos = todos.reduce(
    (acc, todo) => {
      const group = acc[todo.status] || [];
      return {
        ...acc,
        [todo.status]: [...group, todo],
      };
    },
    {} as Record<string, typeof todos>,
  );

  const todoListContent = useMemo(() => {
    if (output?.error) {
      return (
        <div className="flex items-center gap-2 text-red-600">
          <WarningIcon className="size-4" weight="duotone" />
          <span className="text-xs">{output.prompt}</span>
        </div>
      );
    }

    if (!output) {
      return null;
    }

    if (todos.length === 0) {
      return (
        <div className="text-muted-foreground text-xs italic">
          No tasks in the list
        </div>
      );
    }

    return (
      <div className="space-y-3">
        {statusOrder.map((status) => {
          const statusTodos = groupedTodos[status];
          if (!statusTodos || statusTodos.length === 0) return null;

          return (
            <div key={status}>
              <div className="mb-1 font-medium text-muted-foreground text-xs capitalize">
                {status.replace("_", " ")}
              </div>
              <div className="space-y-1">
                {statusTodos.map((todo) => (
                  <div
                    key={todo.id}
                    className={cn(
                      "flex items-start gap-2 rounded-sm p-1.5 text-xs transition-colors",
                      todo.status === "completed" && "opacity-60",
                      todo.status === "inProgress" &&
                        "bg-blue-50 dark:bg-blue-900/20",
                      todo.priority === "high" &&
                        todo.status !== "completed" &&
                        "bg-red-50 dark:bg-red-900/10",
                    )}
                  >
                    {getStatusIcon(todo.status)}
                    <div className="flex-1">
                      <span
                        className={cn(
                          todo.status === "completed" && "line-through",
                        )}
                      >
                        {todo.content}
                      </span>
                    </div>
                    {getPriorityBadge(todo.priority)}
                  </div>
                ))}
              </div>
            </div>
          );
        })}
      </div>
    );
  }, [output, todos.length, groupedTodos]);

  return (
    <Steps data-tool-todo>
      <StepsTrigger
        leftIcon={<KanbanIcon weight="duotone" className="size-4" />}
        loading={isLoading}
        error={output?.status === "error" || Boolean(output?.error)}
      >
        <span>Task List</span>
      </StepsTrigger>
      <StepsContent>
        {isLoading && (
          <StepsItem className="flex items-center gap-2 text-muted-foreground text-xs">
            <Shimmer>
              {output ? "Updating tasks..." : "Loading tasks..."}
            </Shimmer>
          </StepsItem>
        )}
        {errorText && (
          <StepsItem className="text-red-600 text-xs">{errorText}</StepsItem>
        )}
        {todoListContent && <StepsItem>{todoListContent}</StepsItem>}
      </StepsContent>
    </Steps>
  );
}

/* agent-frontmatter:start
AGENT: Edit file tool UI component
PURPOSE: Visualize file edit operations with unified diff view
USAGE: <EditFile part={toolPart} />
EXPORTS: EditFile, EditFileProps
FEATURES:
  - Shows unified diff with syntax highlighting
  - Displays file path and operation type (replace/replaceAll)
  - Uses [!code --] and [!code ++] markers for changes
  - Supports replace_all flag visualization
SEARCHABLE: edit tool, file edit ui, code replacement view, diff view
agent-frontmatter:end */

import { getLanguageFromFilePath } from "@agentstart/utils";
import { SwapIcon } from "@phosphor-icons/react";
import type { Tools } from "agentstart/agent";
import type { InferUITools, ToolUIPart } from "ai";
import { useMemo } from "react";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { CodeBlock } from "../code-block";
import { Shimmer } from "../shimmer";
import { Steps, StepsContent, StepsItem, StepsTrigger } from "../steps";

export interface EditFileProps {
  part: ToolUIPart<InferUITools<Pick<Tools, "edit">>>;
}

export function EditFile({ part: { state, input, output } }: EditFileProps) {
  const fileName = input?.filePath?.split("/").pop() || input?.filePath;

  const language = getLanguageFromFilePath(input?.filePath || "");

  const isLoading = ["input-streaming", "input-available"].includes(state);

  const changes = useMemo(() => {
    if (!input || !input.oldString || !input.newString) return null;

    // Always show as unified diff
    const oldLines = input.oldString.split("\n");
    const newLines = input.newString.split("\n");
    const diffCode = [
      ...oldLines.map((line) => `${line} // [!code --]`),
      ...newLines.map((line) => `${line} // [!code ++]`),
    ].join("\n");

    return (
      <CodeBlock
        code={diffCode}
        copyCode={input.newString}
        language={language}
        showDiff
      />
    );
  }, [input, language]);

  return (
    <Steps data-tool-edit>
      <StepsTrigger
        leftIcon={<SwapIcon weight="duotone" className="size-4" />}
        loading={isLoading}
        error={output?.status === "error" || Boolean(output?.error)}
      >
        <div className="flex items-center gap-2">
          <Tooltip>
            <TooltipTrigger
              render={<code className="text-xs">{fileName}</code>}
            />
            {input?.filePath && (
              <TooltipContent>{input.filePath}</TooltipContent>
            )}
          </Tooltip>
          {input?.replaceAll && (
            <span className="text-muted-foreground text-xs">(replace all)</span>
          )}
        </div>
      </StepsTrigger>
      <StepsContent>
        {isLoading && (
          <StepsItem className="flex items-center gap-2 text-muted-foreground text-xs">
            <Shimmer>Editing file...</Shimmer>
          </StepsItem>
        )}
        <StepsItem>
          <div className="flex items-center gap-2">
            {input?.replaceAll && (
              <span className="text-muted-foreground text-xs">
                (replace all)
              </span>
            )}
          </div>
        </StepsItem>
        {changes && <StepsItem>{changes}</StepsItem>}
        {output?.error?.message && (
          <StepsItem className="text-red-600 text-xs">
            {output.error.message}
          </StepsItem>
        )}
      </StepsContent>
    </Steps>
  );
}

/* agent-frontmatter:start
AGENT: Write file tool UI component
PURPOSE: Display file writing operations with content preview
USAGE: <WriteFile part={toolPart} />
EXPORTS: WriteFile, WriteFileProps
FEATURES:
  - Shows file path and write operation
  - Preview first 10 lines of content with syntax highlighting
  - Indicates when content is truncated
  - Language detection based on file extension
SEARCHABLE: write tool, file creation ui, save file view
agent-frontmatter:end */

import { getLanguageFromFilePath } from "@agentstart/utils";
import { NotePencilIcon } from "@phosphor-icons/react";
import type { Tools } from "agentstart/agent";
import type { InferUITools, ToolUIPart } from "ai";
import { useMemo } from "react";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { CodeBlock } from "../code-block";
import { Shimmer } from "../shimmer";
import { Steps, StepsContent, StepsItem, StepsTrigger } from "../steps";

export interface WriteFileProps {
  part: ToolUIPart<InferUITools<Pick<Tools, "write">>>;
}

export function WriteFile({ part: { state, input, output } }: WriteFileProps) {
  const fileName = input?.filePath?.split("/").pop() || input?.filePath;
  const fileContent = input?.content?.replace(/\\n/g, "\n");
  const isLoading = ["input-streaming", "input-available"].includes(state);

  const preview = useMemo(() => {
    if (!input || !fileContent) return null;
    const lines = fileContent.split("\n");
    const maxLines = 10;
    const hasMore = lines.length > maxLines;
    const language = getLanguageFromFilePath(input.filePath);

    const previewLines = lines.slice(0, maxLines);

    // Generate diff code with all lines marked as additions
    const diffLines = previewLines.map((line) =>
      line.trim() ? `${line} // [!code ++]` : "",
    );
    if (hasMore) {
      diffLines.push(`// ... and ${lines.length - maxLines} more lines`);
    }
    const diffCode = diffLines.join("\n");

    return (
      <CodeBlock
        code={diffCode}
        copyCode={fileContent}
        language={language}
        showDiff
      />
    );
  }, [input, fileContent]);

  const title = useMemo(() => {
    if (!input || !fileContent) return null;

    const lineNumber = fileContent.split("\n").length ?? 0;
    return (
      <div className="flex items-center gap-2">
        <Tooltip>
          <TooltipTrigger render={<span>{fileName}</span>} />
          {input?.filePath && <TooltipContent>{input.filePath}</TooltipContent>}
        </Tooltip>
        <span className="text-green-600">(new) +{lineNumber}</span>
      </div>
    );
  }, [input, fileName, fileContent]);

  return (
    <Steps data-tool-write>
      <StepsTrigger
        leftIcon={<NotePencilIcon weight="duotone" className="size-4" />}
        loading={isLoading}
        error={output?.status === "error" || Boolean(output?.error)}
      >
        {title}
      </StepsTrigger>
      <StepsContent>
        {isLoading && (
          <StepsItem className="flex items-center gap-2 text-muted-foreground text-xs">
            <Shimmer>Writing file...</Shimmer>
          </StepsItem>
        )}
        {preview && <StepsItem>{preview}</StepsItem>}
        {output?.error?.message && (
          <StepsItem className="text-red-600 text-xs">
            {output.error.message}
          </StepsItem>
        )}
      </StepsContent>
    </Steps>
  );
}
