/* agent-frontmatter:start
AGENT: Sidebar layout
PURPOSE: Compose a shadcn sidebar that lists AgentStart threads using TanStack Query
USAGE: <Sidebar>{mainContent}</Sidebar>
EXPORTS: Sidebar, SidebarProps
FEATURES:
  - Fetches threads via TanStack Query with automatic caching and refetching
  - Supports infinite scroll pagination and thread actions
  - Wraps children inside <SidebarInset> for a ready-to-use layout
SEARCHABLE: agent layout, sidebar, agent threads list, tanstack query
agent-frontmatter:end */

"use client";

import {
  DotsThreeIcon,
  EmptyIcon,
  PencilSimpleIcon,
  TrashIcon,
  WarningCircleIcon,
} from "@phosphor-icons/react";
import { useMutation, useQuery } from "@tanstack/react-query";
import { useAgentStartContext } from "agentstart/client";
import type { DBThread } from "agentstart/memory";
import { type ReactNode, useMemo } from "react";
import { Button } from "@/components/ui/button";
import {
  Empty,
  EmptyContent,
  EmptyDescription,
  EmptyHeader,
  EmptyMedia,
  EmptyTitle,
} from "@/components/ui/empty";
import { Menu, MenuItem, MenuPopup, MenuTrigger } from "@/components/ui/menu";
import {
  Sidebar as ShadcnSidebar,
  SidebarInset,
  SidebarMenuSkeleton,
  SidebarProvider,
  SidebarRail,
} from "@/components/ui/sidebar";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { cn } from "@/lib/utils";
import { SimpleThemeSwitch } from "../theme-switch";
import { SidebarContent } from "./sidebar-content";
import { SidebarFooter } from "./sidebar-footer";
import { SidebarHeader } from "./sidebar-header";
import { SidebarItem } from "./sidebar-item";

export type SidebarProps = {
  children?: ReactNode;
  className?: string;
  // Query configuration
  pageSize?: number;
  // UI customization
  header?: {
    title?: string;
  };
  footer?: ReactNode;
  emptyState?: ReactNode;
  errorState?: (error: Error, retry: () => void) => ReactNode;
  // Sidebar configuration
  sidebar?: {
    variant?: "sidebar" | "floating" | "inset";
    side?: "left" | "right";
    collapsible?: "offcanvas" | "icon" | "none";
    className?: string;
    defaultOpen?: boolean;
    open?: boolean;
    onOpenChange?: (open: boolean) => void;
  };
};

export function Sidebar({
  children,
  className,
  pageSize = 20,
  header,
  footer = <SimpleThemeSwitch />,
  emptyState,
  errorState,
  sidebar,
}: SidebarProps) {
  const { orpc, threadId, navigate } = useAgentStartContext();

  // Infinite query for thread list with pagination
  const { data, error, isLoading, isError, refetch } = useQuery(
    orpc.thread.list.queryOptions({
      input: { pageSize },
    }),
  );

  // Rename mutation
  const updateMutation = useMutation(
    orpc.thread.update.mutationOptions({
      onSuccess: () => {
        refetch();
      },
    }),
  );

  // Delete mutation
  const deleteMutation = useMutation(
    orpc.thread.delete.mutationOptions({
      onSuccess: () => {
        // Invalidate and refetch the thread list
        refetch();
      },
    }),
  );

  // Flatten all pages into a single array of threads
  const threads = useMemo(() => {
    if (!data) return [];
    return data.threads.map((thread) => normalizeThread(thread));
  }, [data]);

  const renderThreads = useMemo(() => {
    if (isLoading && threads.length === 0) {
      return Array.from({ length: 6 }).map((_, index) => (
        <SidebarMenuSkeleton key={`thread-skeleton-${index}`} showIcon />
      ));
    }

    if (isError && error) {
      if (errorState) {
        return errorState(error, refetch);
      }
      return (
        <Empty>
          <EmptyHeader>
            <EmptyMedia variant="icon">
              <WarningCircleIcon
                weight="duotone"
                className="size-5 text-destructive"
              />
            </EmptyMedia>
            <EmptyTitle>Failed to load threads</EmptyTitle>
            <EmptyDescription>{error.message}</EmptyDescription>
          </EmptyHeader>
          <EmptyContent>
            <Button
              type="button"
              variant="ghost"
              size="sm"
              onClick={() => refetch()}
            >
              Retry
            </Button>
          </EmptyContent>
        </Empty>
      );
    }

    if (threads.length === 0) {
      if (emptyState) {
        return emptyState;
      }
      return (
        <Empty>
          <EmptyHeader>
            <EmptyMedia variant="icon">
              <EmptyIcon
                weight="duotone"
                className="size-5 text-muted-foreground"
              />
            </EmptyMedia>
            <EmptyTitle>No threads</EmptyTitle>
            <EmptyDescription>
              Start chatting with the agent to see conversations here.
            </EmptyDescription>
          </EmptyHeader>
        </Empty>
      );
    }

    const items = threads.map((thread) => (
      <SidebarItem
        key={thread.id}
        thread={thread}
        isActive={thread.id === threadId}
        onSelect={({ id }) => navigate(`/thread/${id}`)}
        leading={<ThreadAvatar title={thread.title} />}
        trailing={
          <MoreOptions
            threadTitle={thread.title ?? "Thread"}
            onRename={(title) =>
              updateMutation.mutate({
                threadId: thread.id,
                data: { title },
              })
            }
            onDelete={() => deleteMutation.mutate({ threadId: thread.id })}
          />
        }
      />
    ));

    return items;
  }, [
    emptyState,
    error,
    errorState,
    refetch,
    isError,
    isLoading,
    threads,
    deleteMutation.mutate,
    updateMutation.mutate,
  ]);

  return (
    <SidebarProvider
      defaultOpen={sidebar?.defaultOpen}
      open={sidebar?.open}
      onOpenChange={sidebar?.onOpenChange}
      className={className}
    >
      <ShadcnSidebar
        variant={sidebar?.variant ?? "sidebar"}
        side={sidebar?.side ?? "left"}
        collapsible={sidebar?.collapsible ?? "icon"}
        className={cn(
          sidebar?.side === "right" ? "border-l" : "border-r",
          "border-sidebar-border/60",
          sidebar?.className,
        )}
      >
        <SidebarHeader title={header?.title} />
        <SidebarContent>{renderThreads}</SidebarContent>
        <SidebarFooter footer={footer} />
        <SidebarRail />
      </ShadcnSidebar>
      <SidebarInset className="h-screen overflow-hidden bg-background">
        {children}
      </SidebarInset>
    </SidebarProvider>
  );
}

function normalizeThread(thread: DBThread): DBThread {
  return {
    ...thread,
    createdAt:
      thread.createdAt instanceof Date
        ? thread.createdAt
        : new Date(thread.createdAt),
    updatedAt:
      thread.updatedAt instanceof Date
        ? thread.updatedAt
        : new Date(thread.updatedAt),
  };
}

function ThreadAvatar({ title }: { title?: string | null }) {
  const displayTitle = title || "Thread";
  const initials = displayTitle
    .split(" ")
    .filter(Boolean)
    .map((word) => word[0]?.toUpperCase())
    .join("")
    .slice(0, 2);

  return (
    <div className="flex size-6 items-center justify-center rounded-md border border-sidebar-border bg-sidebar-accent/40 font-semibold text-[11px] text-sidebar-foreground uppercase">
      {initials || "AI"}
    </div>
  );
}

function MoreOptions({
  threadTitle,
  onRename,
  onDelete,
}: {
  threadTitle: string;
  onRename: (title: string) => void;
  onDelete: () => void;
}) {
  const handleRename = () => {
    const newTitle = window.prompt("Enter new thread title:", threadTitle);
    if (newTitle?.trim() && newTitle !== threadTitle) {
      onRename(newTitle.trim());
    }
  };

  const handleDelete = () => {
    if (window.confirm(`Are you sure you want to delete "${threadTitle}"?`)) {
      onDelete();
    }
  };

  return (
    <Menu>
      <Tooltip>
        <MenuTrigger
          render={
            <TooltipTrigger
              render={
                <div className="flex size-5 cursor-pointer items-center justify-center rounded-md hover:bg-gray-200 dark:hover:bg-neutral-700">
                  <DotsThreeIcon className="size-4" />
                </div>
              }
            />
          }
        />
        <TooltipContent>
          <span>More options</span>
        </TooltipContent>
      </Tooltip>

      <MenuPopup>
        <MenuItem onClick={handleRename}>
          <PencilSimpleIcon weight="duotone" className="size-4.5" />{" "}
          <span>Rename</span>
        </MenuItem>
        <MenuItem onClick={handleDelete}>
          <TrashIcon
            weight="duotone"
            className="size-4.5 text-destructive"
            color="currentColor"
          />{" "}
          <span className="text-destructive">Delete</span>
        </MenuItem>
      </MenuPopup>
    </Menu>
  );
}

/* agent-frontmatter:start
AGENT: Sidebar item
PURPOSE: Render a single thread row inside the agent sidebar
USAGE: <SidebarItem thread={thread} onSelect={handleSelect} />
EXPORTS: SidebarItem, SidebarItemProps
FEATURES:
  - Displays title, timestamp, and optional leading/trailing content
  - Supports active and disabled states for navigation
  - Computes human-readable relative timestamps by default
SEARCHABLE: agent layout, list item, sidebar row
agent-frontmatter:end */

"use client";

import { formatRelativeFromNow } from "@agentstart/utils";
import type { DBThread } from "agentstart/memory";
import type { ReactNode } from "react";
import { memo, useMemo } from "react";
import { SidebarMenuButton, SidebarMenuItem } from "@/components/ui/sidebar";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";

export type SidebarItemProps = {
  thread: DBThread;
  leading?: ReactNode;
  trailing?: ReactNode;
  secondaryText?: ReactNode;
  isActive?: boolean;
  disabled?: boolean;
  className?: string;
  onSelect?: (thread: DBThread) => void;
};

export const SidebarItem = memo(function SidebarItem({
  thread,
  leading,
  trailing,
  secondaryText,
  isActive = false,
  disabled = false,
  className,
  onSelect,
}: SidebarItemProps) {
  const computedSecondaryText = useMemo(() => {
    if (secondaryText === null) {
      return null;
    }
    if (secondaryText !== undefined) {
      return secondaryText;
    }
    return formatRelativeFromNow(thread.updatedAt ?? thread.createdAt);
  }, [secondaryText, thread.createdAt, thread.updatedAt]);

  return (
    <SidebarMenuItem className={className}>
      <SidebarMenuButton
        type="button"
        isActive={isActive}
        disabled={disabled}
        onClick={() => {
          if (!disabled) {
            onSelect?.(thread);
          }
        }}
        className="group/sidebar-item cursor-pointer"
      >
        <div className="flex w-full items-center gap-3 overflow-hidden">
          {leading && <div className="shrink-0">{leading}</div>}
          <Tooltip>
            <TooltipTrigger
              render={
                <span className="flex-1 truncate font-medium text-sm">
                  {thread.title || "Untitled thread"}
                </span>
              }
            />
            {computedSecondaryText && (
              <TooltipContent className="max-w-xs">
                <span className="space-x-1">
                  <span>{thread.title || "Untitled thread"}</span>
                  <span className="text-[10px] text-muted-foreground">
                    {computedSecondaryText}
                  </span>
                </span>
              </TooltipContent>
            )}
          </Tooltip>
          {trailing && (
            <div className="invisible shrink-0 group-hover/sidebar-item:visible">
              {trailing}
            </div>
          )}
        </div>
      </SidebarMenuButton>
    </SidebarMenuItem>
  );
});

/* agent-frontmatter:start
AGENT: Sidebar header
PURPOSE: Render the agent sidebar header with controls and actions
USAGE: <SidebarHeader title={title} />
EXPORTS: SidebarHeader, SidebarHeaderProps
FEATURES:
  - Displays title and sidebar toggle control
  - Provides "New Thread" action button with navigation
  - Responsive layout with conditional title visibility
SEARCHABLE: agent sidebar, header layout, sidebar controls
agent-frontmatter:end */

"use client";

import { NotePencilIcon, SidebarIcon } from "@phosphor-icons/react";
import { useAgentStartContext } from "agentstart/client";
import type React from "react";
import {
  SidebarHeader as ShadcnSidebarHeader,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  useSidebar,
} from "@/components/ui/sidebar";
import { cn } from "@/lib/utils";

export type SidebarHeaderProps = {
  title: React.ReactNode;
};

export function SidebarHeader({ title }: SidebarHeaderProps) {
  const { navigate } = useAgentStartContext();
  const { open, toggleSidebar } = useSidebar();

  return (
    <ShadcnSidebarHeader>
      <div
        className={cn(
          "flex items-center gap-2",
          open ? "justify-between" : "justify-center",
        )}
      >
        {open && (
          <div className="flex items-center pl-2.5">
            <span className="truncate font-semibold text-sm">
              {title ?? "LOGO"}
            </span>
          </div>
        )}

        <div className="flex items-center gap-1">
          {/* trigger sidebar */}
          <SidebarMenuButton onClick={toggleSidebar}>
            <SidebarIcon
              weight="duotone"
              className="size-4.5! cursor-pointer"
            />
            <span className="sr-only">Toggle sidebar</span>
          </SidebarMenuButton>
        </div>
      </div>

      <SidebarMenu>
        <SidebarMenuItem>
          <SidebarMenuButton
            className="cursor-pointer"
            onClick={() => navigate("/")}
          >
            <NotePencilIcon weight="duotone" className="size-4.5!" />
            <span>New Thread</span>
          </SidebarMenuButton>
        </SidebarMenuItem>
      </SidebarMenu>
    </ShadcnSidebarHeader>
  );
}

/* agent-frontmatter:start
AGENT: Sidebar content
PURPOSE: Render the agent sidebar thread list with scrollable layout
USAGE: <SidebarContent>{threadList}</SidebarContent>
EXPORTS: SidebarContent, SidebarContentProps
FEATURES:
  - Displays thread list with scrollable layout
  - Wraps children in SidebarMenu component
  - Provides consistent spacing and styling
SEARCHABLE: agent sidebar, thread list, sidebar content
agent-frontmatter:end */

"use client";

import type { ReactNode } from "react";
import { ScrollArea } from "@/components/ui/scroll-area";
import {
  SidebarContent as ShadcnSidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarMenu,
  useSidebar,
} from "@/components/ui/sidebar";

export type SidebarContentProps = {
  children: ReactNode;
};

export function SidebarContent({ children }: SidebarContentProps) {
  const { open } = useSidebar();
  if (!open) return <ShadcnSidebarContent />;

  return (
    <ShadcnSidebarContent>
      <SidebarGroup>
        <SidebarGroupLabel>Recent threads</SidebarGroupLabel>
        <SidebarGroupContent>
          <ScrollArea className="h-[calc(100vh-14rem)] md:h-[calc(100vh-12rem)]">
            <SidebarMenu className="w-[calc(var(--sidebar-width)-(var(--spacing)*4))]">
              {children}
            </SidebarMenu>
          </ScrollArea>
        </SidebarGroupContent>
      </SidebarGroup>
    </ShadcnSidebarContent>
  );
}

/* agent-frontmatter:start
AGENT: Sidebar footer
PURPOSE: Render optional footer content for the agent sidebar
USAGE: <SidebarFooter footer={footerContent} />
EXPORTS: SidebarFooter, SidebarFooterProps
FEATURES:
  - Wraps custom footer nodes in the shadcn sidebar footer element
  - Conditionally renders only when footer content is provided
  - Maintains consistent sidebar layout structure
SEARCHABLE: agent sidebar, footer slot, layout footer
agent-frontmatter:end */

"use client";

import type { ReactNode } from "react";
import { SidebarFooter as ShadcnSidebarFooter } from "@/components/ui/sidebar";

export type SidebarFooterProps = {
  footer?: ReactNode;
};

export function SidebarFooter({ footer }: SidebarFooterProps) {
  if (!footer) {
    return null;
  }

  return <ShadcnSidebarFooter>{footer}</ShadcnSidebarFooter>;
}

/* agent-frontmatter:start
AGENT: Theme switcher components
PURPOSE: Toggle between light/dark/system themes
USAGE: <SimpleThemeSwitch /> or <ThemeSwitch />
EXPORTS: SimpleThemeSwitch, ThemeSwitch
FEATURES:
  - Icon button for quick theme toggle (SimpleThemeSwitch)
  - Tab-based theme selector (ThemeSwitch)
  - System theme detection and display
  - Smooth transitions between themes
SEARCHABLE: theme toggle, dark mode, light mode, theme switcher
agent-frontmatter:end */

"use client";

import { MonitorIcon, MoonIcon, SunIcon } from "@phosphor-icons/react";
import { useTheme } from "agentstart/client";
import type React from "react";
import { useEffect, useState } from "react";
import { Button } from "@/components/ui/button";
import { Tabs, TabsList, TabsTrigger } from "@/components/ui/tabs";

export function SimpleThemeSwitch() {
  const { theme, setTheme } = useTheme();
  const [mounted, setMounted] = useState(false);

  // Prevent hydration mismatch - use default until mounted
  useEffect(() => {
    setMounted(true);
  }, []);

  if (!mounted) {
    return (
      <Button
        variant="ghost"
        size="icon"
        className="size-8 rounded-full text-foreground transition-colors"
      >
        <SunIcon weight="duotone" className="size-4" />
      </Button>
    );
  }

  // Resolve system theme to actual theme
  const resolvedTheme =
    theme === "system"
      ? window.matchMedia("(prefers-color-scheme: dark)").matches
        ? "dark"
        : "light"
      : theme;

  const icon =
    resolvedTheme === "dark" ? (
      <MoonIcon weight="duotone" className="size-4" />
    ) : (
      <SunIcon weight="duotone" className="size-4" />
    );

  const nextTheme = resolvedTheme === "light" ? "dark" : "light";

  return (
    <Button
      variant="ghost"
      size="icon"
      className="size-8 rounded-full text-foreground transition-colors"
      onClick={() => {
        setTheme(nextTheme);
      }}
    >
      {icon}
    </Button>
  );
}

export function ThemeSwitch() {
  const { theme, setTheme } = useTheme();
  const [mounted, setMounted] = useState(false);

  // Prevent hydration mismatch - use a default value until mounted
  useEffect(() => {
    setMounted(true);
  }, []);

  const currentTheme = mounted ? theme : "system";

  return (
    <Tabs
      value={currentTheme}
      onValueChange={(value) => setTheme(value as any)}
    >
      <TabsList className="h-8 overflow-hidden">
        <ThemeSwitchTrigger value="system">
          <MonitorIcon className="size-4" />
        </ThemeSwitchTrigger>
        <ThemeSwitchTrigger value="light">
          <SunIcon weight="duotone" className="size-4" />
        </ThemeSwitchTrigger>
        <ThemeSwitchTrigger value="dark">
          <MoonIcon weight="duotone" className="size-4" />
        </ThemeSwitchTrigger>
      </TabsList>
    </Tabs>
  );
}

function ThemeSwitchTrigger({
  children,
  value,
}: React.PropsWithChildren<{ value: string }>) {
  return (
    <TabsTrigger
      className="flex size-[26px] items-center justify-center rounded-full p-0 transition-colors data-[state=active]:border-none data-[state=inactive]:border-none data-[state=active]:bg-white dark:data-[state=active]:bg-input"
      value={value}
    >
      {children}
    </TabsTrigger>
  );
}
