/* agent-frontmatter:start
AGENT: Sidebar layout
PURPOSE: Compose a shadcn sidebar that lists AgentStart threads using TanStack Query
USAGE: <Sidebar>{mainContent}</Sidebar>
EXPORTS: Sidebar, SidebarProps
FEATURES:
  - Fetches threads via TanStack Query with automatic caching and refetching
  - Supports infinite scroll pagination, thread creation, and selection callbacks
  - Wraps children inside <SidebarInset> for a ready-to-use layout
SEARCHABLE: agent layout, sidebar, agent threads list, tanstack query
agent-frontmatter:end */

"use client";

import {
  useInfiniteQuery,
  useMutation,
  useQueryClient,
} from "@tanstack/react-query";
import type { DBThread } from "agentstart/db";
import {
  AlertCircleIcon,
  InboxIcon,
  Loader2Icon,
  PlusIcon,
  RotateCcwIcon,
} from "lucide-react";
import {
  type ReactNode,
  useCallback,
  useEffect,
  useMemo,
  useState,
} from "react";
import { Button } from "@/components/ui/button";
import { ScrollArea } from "@/components/ui/scroll-area";
import {
  Sidebar as ShadcnSidebar,
  SidebarContent,
  SidebarFooter,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarHeader,
  SidebarInset,
  SidebarMenu,
  SidebarMenuBadge,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarMenuSkeleton,
  SidebarProvider,
  SidebarRail,
  SidebarSeparator,
  SidebarTrigger,
} from "@/components/ui/sidebar";
import { cn } from "@/lib/utils";
import { useAgentStartContext } from "../provider";
import { SidebarItem } from "./sidebar-item";

export type SidebarProps = {
  children?: ReactNode;
  className?: string;
  // Thread selection
  selectedThreadId?: string;
  defaultSelectedThreadId?: string;
  autoSelectFirst?: boolean;
  onSelectThread?: (thread: DBThread) => void;
  onThreadCreated?: (thread: DBThread) => void;
  // Query configuration
  pageSize?: number;
  queryKey?: unknown[];
  // UI customization
  header?: {
    title?: string;
    description?: string;
    actions?: ReactNode;
  };
  footer?: ReactNode;
  emptyState?: ReactNode;
  errorState?: (error: Error, retry: () => void) => ReactNode;
  // Sidebar configuration
  sidebar?: {
    variant?: "sidebar" | "floating" | "inset";
    side?: "left" | "right";
    collapsible?: "offcanvas" | "icon" | "none";
    className?: string;
    defaultOpen?: boolean;
    open?: boolean;
    onOpenChange?: (open: boolean) => void;
  };
};

export function Sidebar({
  children,
  className,
  selectedThreadId,
  defaultSelectedThreadId,
  autoSelectFirst = true,
  onSelectThread,
  onThreadCreated,
  pageSize = 20,
  queryKey = ["threads"],
  header,
  footer,
  emptyState,
  errorState,
  sidebar,
}: SidebarProps) {
  const { client } = useAgentStartContext();
  const queryClient = useQueryClient();

  const [internalSelectedId, setInternalSelectedId] = useState<string | null>(
    defaultSelectedThreadId ?? null,
  );

  const showCreateButton = typeof client.thread.create === "function";

  // Infinite query for thread list with pagination
  const {
    data,
    error,
    fetchNextPage,
    hasNextPage,
    isFetching,
    isFetchingNextPage,
    isLoading,
    isError,
    refetch,
  } = useInfiniteQuery({
    queryKey,
    queryFn: async ({ pageParam = 1 }) => {
      const response = await client.thread.list({
        page: pageParam,
        pageSize,
      });
      return response;
    },
    initialPageParam: 1,
    getNextPageParam: (lastPage) => {
      if (lastPage.pageInfo.hasNextPage) {
        return lastPage.pageInfo.page + 1;
      }
      return undefined;
    },
  });

  // Mutation for creating new thread
  const createThreadMutation = useMutation({
    mutationFn: async () => {
      if (!client.thread.create) {
        throw new Error("Thread creation is not supported");
      }
      const result = await client.thread.create();
      return result.thread;
    },
    onSuccess: (thread) => {
      // Invalidate and refetch threads
      queryClient.invalidateQueries({ queryKey });
      setInternalSelectedId(thread.id);
      onThreadCreated?.(thread);
      onSelectThread?.(thread);
    },
  });

  // Flatten all pages into a single array of threads
  const threads = useMemo(() => {
    if (!data) return [];
    return data.pages.flatMap((page) =>
      page.threads.map((thread) => normalizeThread(thread)),
    );
  }, [data]);

  // Auto-select first thread
  useEffect(() => {
    if (selectedThreadId !== undefined) {
      setInternalSelectedId(selectedThreadId);
    }
  }, [selectedThreadId]);

  useEffect(() => {
    if (
      autoSelectFirst &&
      selectedThreadId === undefined &&
      !internalSelectedId &&
      threads.length > 0
    ) {
      const firstThread = threads[0];
      if (!firstThread) {
        return;
      }
      setInternalSelectedId(firstThread.id);
      onSelectThread?.(firstThread);
    }
  }, [
    autoSelectFirst,
    internalSelectedId,
    onSelectThread,
    selectedThreadId,
    threads,
  ]);

  // Clear selection if thread no longer exists
  useEffect(() => {
    if (
      internalSelectedId &&
      threads.length > 0 &&
      !threads.some((thread) => thread.id === internalSelectedId)
    ) {
      setInternalSelectedId(null);
    }
  }, [internalSelectedId, threads]);

  const activeThreadId = selectedThreadId ?? internalSelectedId ?? null;

  const handleSelectThread = useCallback(
    (thread: DBThread) => {
      if (selectedThreadId === undefined) {
        setInternalSelectedId(thread.id);
      }
      onSelectThread?.(thread);
    },
    [onSelectThread, selectedThreadId],
  );

  const renderThreads = useMemo(() => {
    if (isLoading && threads.length === 0) {
      return Array.from({ length: 6 }).map((_, index) => (
        <SidebarMenuSkeleton key={`thread-skeleton-${index}`} showIcon />
      ));
    }

    if (isError && error) {
      if (errorState) {
        return errorState(error, refetch);
      }
      return (
        <SidebarMenuItem>
          <SidebarMenuButton
            type="button"
            variant="outline"
            className="items-start gap-3 text-left"
            onClick={() => refetch()}
          >
            <AlertCircleIcon className="mt-0.5 size-4 text-destructive" />
            <div className="space-y-1">
              <p className="font-medium text-destructive text-sm">
                Failed to load threads
              </p>
              <p className="text-muted-foreground text-xs">{error.message}</p>
              <Button
                type="button"
                variant="ghost"
                size="sm"
                className="h-7 justify-start px-0 text-xs"
                onClick={() => refetch()}
              >
                Retry
              </Button>
            </div>
          </SidebarMenuButton>
        </SidebarMenuItem>
      );
    }

    if (threads.length === 0) {
      if (emptyState) {
        return emptyState;
      }
      return (
        <SidebarMenuItem>
          <SidebarMenuButton
            type="button"
            disabled
            className="items-start gap-3 text-left"
          >
            <InboxIcon className="mt-0.5 size-4 text-muted-foreground" />
            <div className="space-y-1">
              <p className="font-medium text-sm">No threads yet</p>
              <p className="text-muted-foreground text-xs">
                Start chatting with the agent to see conversations here.
              </p>
            </div>
          </SidebarMenuButton>
        </SidebarMenuItem>
      );
    }

    const items = threads.map((thread) => (
      <SidebarItem
        key={thread.id}
        thread={thread}
        isActive={thread.id === activeThreadId}
        onSelect={handleSelectThread}
        leading={<ThreadAvatar title={thread.title} />}
        trailing={
          thread.visibility === "public" ? (
            <SidebarMenuBadge className="uppercase">Public</SidebarMenuBadge>
          ) : null
        }
      />
    ));

    if (isFetching && threads.length > 0) {
      items.push(
        ...Array.from({ length: 3 }).map((_, index) => (
          <SidebarMenuSkeleton
            key={`thread-refresh-skeleton-${index}`}
            showIcon
          />
        )),
      );
    }

    return items;
  }, [
    activeThreadId,
    emptyState,
    error,
    errorState,
    refetch,
    handleSelectThread,
    isError,
    isFetching,
    isLoading,
    threads,
  ]);

  return (
    <SidebarProvider
      defaultOpen={sidebar?.defaultOpen}
      open={sidebar?.open}
      onOpenChange={sidebar?.onOpenChange}
      className={className}
    >
      <ShadcnSidebar
        variant={sidebar?.variant ?? "sidebar"}
        side={sidebar?.side ?? "left"}
        collapsible={sidebar?.collapsible ?? "icon"}
        className={cn(
          sidebar?.side === "right" ? "border-l" : "border-r",
          "border-sidebar-border/60",
          sidebar?.className,
        )}
      >
        <SidebarHeader className="border-sidebar-border border-b px-3 py-4">
          <div className="flex items-center justify-between gap-2">
            <div className="flex items-center gap-2">
              <SidebarTrigger className="md:hidden" />
              <div className="flex min-w-0 flex-col">
                <span className="truncate font-semibold text-sm">
                  {header?.title ?? "Threads"}
                </span>
                {header?.description ? (
                  <span className="truncate text-muted-foreground text-xs">
                    {header.description}
                  </span>
                ) : null}
              </div>
            </div>
            <div className="flex items-center gap-1">
              <Button
                type="button"
                variant="ghost"
                size="icon"
                onClick={() => refetch()}
                disabled={isFetching}
              >
                <RotateCcwIcon
                  className={cn("size-4", isFetching && "animate-spin")}
                />
                <span className="sr-only">Refresh threads</span>
              </Button>
              {showCreateButton ? (
                <Button
                  type="button"
                  size="icon"
                  onClick={() => createThreadMutation.mutate()}
                  disabled={createThreadMutation.isPending}
                >
                  {createThreadMutation.isPending ? (
                    <Loader2Icon className="size-4 animate-spin" />
                  ) : (
                    <PlusIcon className="size-4" />
                  )}
                  <span className="sr-only">Create thread</span>
                </Button>
              ) : null}
              {header?.actions}
            </div>
          </div>
        </SidebarHeader>
        <SidebarContent>
          <SidebarGroup>
            <SidebarGroupLabel>Recent conversations</SidebarGroupLabel>
            <SidebarGroupContent>
              <ScrollArea className="h-[calc(100vh-14rem)] pr-2 md:h-[calc(100vh-12rem)]">
                <SidebarMenu>{renderThreads}</SidebarMenu>
              </ScrollArea>
            </SidebarGroupContent>
          </SidebarGroup>
          {hasNextPage ? (
            <>
              <SidebarSeparator />
              <SidebarGroup>
                <SidebarGroupContent>
                  <SidebarMenu>
                    <SidebarMenuItem>
                      <SidebarMenuButton
                        type="button"
                        variant="outline"
                        disabled={isFetchingNextPage}
                        onClick={() => {
                          if (hasNextPage && !isFetchingNextPage) {
                            fetchNextPage();
                          }
                        }}
                      >
                        {isFetchingNextPage ? (
                          <>
                            <Loader2Icon className="mr-2 size-4 animate-spin" />
                            Loading moreâ€¦
                          </>
                        ) : (
                          "Load more"
                        )}
                      </SidebarMenuButton>
                    </SidebarMenuItem>
                  </SidebarMenu>
                </SidebarGroupContent>
              </SidebarGroup>
            </>
          ) : null}
        </SidebarContent>
        {footer ? <SidebarFooter>{footer}</SidebarFooter> : null}
        <SidebarRail />
      </ShadcnSidebar>
      <SidebarInset>{children}</SidebarInset>
    </SidebarProvider>
  );
}

function normalizeThread(thread: DBThread): DBThread {
  const createdAt =
    thread.createdAt instanceof Date
      ? thread.createdAt
      : new Date(thread.createdAt);
  const updatedAt =
    thread.updatedAt instanceof Date
      ? thread.updatedAt
      : new Date(thread.updatedAt);

  return {
    ...thread,
    createdAt,
    updatedAt,
  };
}

function ThreadAvatar({ title }: { title?: string | null }) {
  const initials = (title ?? "Thread")
    .split(" ")
    .filter(Boolean)
    .map((segment) => segment[0]?.toUpperCase())
    .join("")
    .slice(0, 2);

  return (
    <div className="flex size-8 items-center justify-center rounded-md border border-sidebar-border bg-sidebar-accent/40 font-semibold text-[11px] text-sidebar-foreground uppercase">
      {initials || "AI"}
    </div>
  );
}

/* agent-frontmatter:start
AGENT: Sidebar item
PURPOSE: Render a single thread row inside the agent sidebar
USAGE: <SidebarItem thread={thread} onSelect={handleSelect} />
EXPORTS: SidebarItem, SidebarItemProps
FEATURES:
  - Displays title, timestamp, and optional leading/trailing content
  - Supports active and disabled states for navigation
  - Computes human-readable relative timestamps by default
SEARCHABLE: agent layout, list item, sidebar row
agent-frontmatter:end */

"use client";

import { formatRelativeFromNow } from "@agentstart/utils";
import type { DBThread } from "agentstart/db";
import type { ReactNode } from "react";
import { memo, useMemo } from "react";
import { SidebarMenuButton, SidebarMenuItem } from "@/components/ui/sidebar";

export type SidebarItemProps = {
  thread: DBThread;
  leading?: ReactNode;
  trailing?: ReactNode;
  secondaryText?: ReactNode;
  isActive?: boolean;
  disabled?: boolean;
  className?: string;
  onSelect?: (thread: DBThread) => void;
};

export const SidebarItem = memo(function SidebarItem({
  thread,
  leading,
  trailing,
  secondaryText,
  isActive = false,
  disabled = false,
  className,
  onSelect,
}: SidebarItemProps) {
  const computedSecondaryText = useMemo(() => {
    if (secondaryText === null) {
      return null;
    }
    if (secondaryText !== undefined) {
      return secondaryText;
    }
    return formatRelativeFromNow(thread.updatedAt ?? thread.createdAt);
  }, [secondaryText, thread.createdAt, thread.updatedAt]);

  return (
    <SidebarMenuItem className={className}>
      <SidebarMenuButton
        type="button"
        isActive={isActive}
        disabled={disabled}
        onClick={() => {
          if (!disabled) {
            onSelect?.(thread);
          }
        }}
      >
        <div className="flex w-full items-center gap-3">
          {leading ? <div className="shrink-0">{leading}</div> : null}
          <div className="flex min-w-0 flex-1 flex-col text-left">
            <span className="truncate font-medium text-sm">
              {thread.title || "Untitled thread"}
            </span>
            {computedSecondaryText ? (
              <span className="truncate text-muted-foreground text-xs">
                {computedSecondaryText}
              </span>
            ) : null}
          </div>
          {trailing ? <div className="shrink-0">{trailing}</div> : null}
        </div>
      </SidebarMenuButton>
    </SidebarMenuItem>
  );
});
